function submitStation(){var n=$("#selected-station").val(),t=$(this).data("url");$.get(t,{selectedStation:n},function(n){var t=$("#departures");$.each(n.departureModels,function(n,i){var r='<div class="col-md-3"><label>Cancellation reason:<\/label><span>'+i.cancellationReason+"<\/span><label>Delay reason:<\/label><span>"+i.delayReason+"<\/span><label>Destination:<\/label><span>"+i.destinationName+"<\/span><label>Due:<\/label><span>"+i.due+"<\/span><label>Expected:<\/label><span>"+i.expected+"<\/span><label>Operator:<\/label><span>"+i.operator+"<\/span><\/div>";t.append(r)})})}$(document).ready(function(){$("#station").autocomplete({source:function(n,t){$.ajax({url:"/Home/SearchStations",dataType:"json",data:{term:n.term},success:function(n){t(n)}})},minLength:1,select:function(n,t){$("#station").text(t.item.value);$("#selected-station").val(t.item.key)}});$("#submit-station").click(submitStation)});